const React = require('react');
const createReactClass = require('create-react-class');

const { Dialog } = require('material-ui/Dialog');
const { FlatButton } = require('material-ui/FlatButton');
const { Paper } = require('material-ui/Paper');

const ExperiencePaper = createReactClass({
  displayName: 'ExperiencePaper',
  getInitialState() {
    return {
      open: false
    };
  },
  handleOpen() {
    this.setState({ open: true });
  },
  handleClose() {
    this.setState({ open: false });
  },
  render() {
    const customDialogStyle = {
      width: '100%',
      maxWidth: '700px',
      padding: '40px'
    };

    const {
      title,
      job,
      description1,
      description2,
      description3,
      description4
    } = this.props;
    const actions = [
      <FlatButton label="Close" primary onClick={this.handleClose} />
    ];

    const imageVal = this.props.image[Object.keys(this.props.image)[0]];

    return (
      <React.Fragment>
        <Paper zDepth={1} className="exp-image-paper" onClick={this.handleOpen}>
          <div className="bg">
            <img src={imageVal} alt="asdfasd" />
            <div className="overlay">
              <h2>{this.props.title}</h2>
            </div>
            <Dialog
              contentStyle={customDialogStyle}
              title={this.props.job}
              actions={actions}
              modal={false}
              open={this.state.open}
              onRequestClose={this.handleClose}
            >
              <p className="job-description">
                {this.props.description1} <br />
                <br />
                {this.props.description2} <br />
                <br />
                {this.props.description3} <br />
                <br />
                {this.props.description4}
              </p>
            </Dialog>
          </div>
        </Paper>
      </React.Fragment>
    );
  }
});

module.exports(ExperiencePaper);
